html, body {
  min-height: 100vh;
}

.bs-callout {
  position: fixed;
  bottom: 20px;
  right: 20px;
  padding: 15px;
  margin-bottom: 20px;
  border-radius: 4px;
  margin: 0;
  box-shadow: 0 4px 7px -1px rgba(0,0,0,0.2);
}

.bs-callout-success {
  color: #3c763d;
  background-color: #dff0d8;
  border-color: #d6e9c6;
  border: 1px solid #d6e9c6;
  border-left: 5px solid #3c763d;
}

#operations-list {
  height: calc(100vh - 400px) !important;
}

#btns-container {
  position: absolute;
  top: 70px;
  left: 30px;
  line-height: 2;
  font-weight: 700;
}

#btns-container .btn {
  margin-right: 10px;
}

/* Backgrounds */

.member-cards-board {
    background: #131719 !important;
}
