  }
  :not(#z) .bg-color-18 {
    background-color: var(--violet) !important;
    filter: hue-rotate(40deg);
  }
  :not(#z) .bg-color-19 {
    background-color: var(--magenta) !important;
    filter: hue-rotate(40deg);
  }
  :not(#z) .bg-color-20 {
    background-color: var(--red) !important;
    filter: hue-rotate(40deg);
  }
  @keyframes pulse {
    40% {
      opacity: 0;
    }
    80% {
      opacity: 1;
    }
  }

