#pomodoro-timer {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
}

#pomodoro-timer .wrapper {
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
}

#pomodoro-timer .wrapper .detail {
    height: auto;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

#pomodoro-timer .wrapper .detail .time {
    font-size: 100px;
    line-height: 1.3;
}

#pomodoro-timer .wrapper .detail .title {
    font-size: 40px;
    max-width: none;
    line-height: 1.3;
}

#pomodoro-timer .wrapper .icons {
    position: relative;
}

#pomodoro-timer .wrapper .pomodoro-settings svg {
    width: 35px;
    height: 35px;
}

#pomodoro-timer .wrapper .pomodoro-settings {
    margin-top: 10px;
    margin-left: 15px;
}
